contracts/SigLending.sol.content:
//SPDX-License-Identifier: Unlicensed
pragma solidity 0.8.13;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./interfaces/niftyapes/lending/ILending.sol";
import "./interfaces/niftyapes/sigLending/ISigLending.sol";
import "./interfaces/niftyapes/offers/IOffers.sol";

/// @title Implementation of the ILending interface
contract NiftyApesSigLending is OwnableUpgradeable, PausableUpgradeable, ReentrancyGuardUpgradeable, ISigLending {
  /// @inheritdoc ISigLending
  address public offersContractAddress;

  /// @inheritdoc ISigLending
  address public lendingContractAddress;

  /// @dev This empty reserved space is put in place to allow future versions to add new
  /// variables without shifting storage.
  uint256[500] private __gap;

  /// @notice The initializer for the NiftyApes protocol.
  ///         Nifty Apes is intended to be deployed behind a proxy amd thus needs to initialize
  ///         its state outsize of a constructor.
  function initialize(address newOffersContractAddress) public initializer {
    offersContractAddress = newOffersContractAddress;

    OwnableUpgradeable.__Ownable_init();
    PausableUpgradeable.__Pausable_init();
    ReentrancyGuardUpgradeable.__ReentrancyGuard_init();
  }

  /// @inheritdoc ISigLendingAdmin
  function updateLendingContractAddress(address newLendingContractAddress) external onlyOwner {
    emit SigLendingXLendingContractAddressUpdated(lendingContractAddress, newLendingContractAddress);
    lendingContractAddress = newLendingContractAddress;
  }

  /// @inheritdoc ISigLendingAdmin
  function pause() external onlyOwner {
    _pause();
  }

  /// @inheritdoc ISigLendingAdmin
  function unpause() external onlyOwner {
    _unpause();
  }

  // @inheritdoc ISigLending
  function executeLoanByBorrowerSignature(
    Offer memory offer,
    bytes memory signature,
    uint256 nftId
  ) external payable whenNotPaused nonReentrant {
    address lender = IOffers(offersContractAddress).getOfferSigner(offer, signature);

    _requireOfferCreator(offer, lender);
    IOffers(offersContractAddress).requireAvailableSignature(signature);
    IOffers(offersContractAddress).requireSignature65(signature);
    IOffers(offersContractAddress).requireMinimumDuration(offer);
    _requireLenderOffer(offer);

    if (!offer.floorTerm) {
      _requireMatchingNftId(offer, nftId);
      IOffers(offersContractAddress).markSignatureUsed(offer, signature);
    }

    // execute state changes for executeLoanByBid
    ILending(lendingContractAddress).doExecuteLoan(offer, lender, msg.sender, nftId);
  }

  // @inheritdoc ISigLending
  function executeLoanByLenderSignature(Offer memory offer, bytes memory signature) external payable whenNotPaused nonReentrant {
    address borrower = IOffers(offersContractAddress).getOfferSigner(offer, signature);

    _requireOfferCreator(offer, borrower);
    IOffers(offersContractAddress).requireAvailableSignature(signature);
    IOffers(offersContractAddress).requireSignature65(signature);
    IOffers(offersContractAddress).requireMinimumDuration(offer);
    _requireBorrowerOffer(offer);
    IOffers(offersContractAddress).requireNoFloorTerms(offer);

    IOffers(offersContractAddress).markSignatureUsed(offer, signature);

    ILending(lendingContractAddress).doExecuteLoan(offer, msg.sender, borrower, offer.nftId);
  }

  // @inheritdoc ISigLending
  function refinanceByBorrowerSignature(
    Offer memory offer,
    bytes memory signature,
    uint256 nftId,
    uint32 expectedLastUpdatedTimestamp
  ) external whenNotPaused nonReentrant {
    address signer = IOffers(offersContractAddress).getOfferSigner(offer, signature);

    _requireOfferCreator(offer, signer);
    IOffers(offersContractAddress).requireAvailableSignature(signature);
    IOffers(offersContractAddress).requireSignature65(signature);
    IOffers(offersContractAddress).requireMinimumDuration(offer);

    if (!offer.floorTerm) {
      _requireMatchingNftId(offer, nftId);
      IOffers(offersContractAddress).markSignatureUsed(offer, signature);
    }

    ILending(lendingContractAddress).doRefinanceByBorrower(offer, nftId, msg.sender, expectedLastUpdatedTimestamp);
  }

  function _requireLenderOffer(Offer memory offer) internal pure {
    require(offer.lenderOffer, "00012");
  }

  function _requireBorrowerOffer(Offer memory offer) internal pure {
    require(!offer.lenderOffer, "00013");
  }

  function _requireMatchingNftId(Offer memory offer, uint256 nftId) internal pure {
    require(nftId == offer.nftId, "00022");
  }

  function _requireOfferCreator(Offer memory offer, address creator) internal pure {
    require(creator == offer.creator, "00024");
  }

  // solhint-disable-next-line no-empty-blocks
  function renounceOwnership() public override onlyOwner {}
}

contracts/interfaces/niftyapes/sigLendin.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "./ISigLendingAdmin.sol";
import "./ISigLendingEvents.sol";
import "../offers/IOffersStructs.sol";

/// @title The signature lending interface for Nifty Apes
///        This interface is intended to be used for interacting with loans on the protocol.
interface ISigLending is ISigLendingAdmin, ISigLendingEvents, IOffersStructs {
    /// @notice Returns the address for the associated offers contract
    function offersContractAddress() external view returns (address);

    /// @notice Returns the address for the associated liquidity contract
    function lendingContractAddress() external view returns (address);

    /// @notice Start a loan as the borrower using a signed offer.
    ///         The caller of this method has to be the current owner of the NFT
    ///         Since offers can be floorTerm offers they might not include a specific nft id,
    ///         thus the caller has to pass an extra nft id to the method to identify the nft.
    /// @param offer The details of the loan auction offer
    /// @param signature A signed offerHash
    /// @param nftId The id of a specified NFT
    function executeLoanByBorrowerSignature(
        Offer calldata offer,
        bytes memory signature,
        uint256 nftId
    ) external payable;

    /// @notice Start a loan as the lender using a borrowers offer and signature.
    ///         Borrowers can make offers for loan terms on their NFTs and thus lenders can
    ///         execute these offers
    /// @param offer The details of the loan auction offer
    /// @param signature A signed offerHash
    function executeLoanByLenderSignature(Offer calldata offer, bytes calldata signature)
        external
        payable;

    /// @notice Refinance a loan against an off chain signed offer as the borrower.
    ///         The new offer has to cover the principle remaining and all lender interest owed on the loan
    ///         Borrowers can refinance at any time even after loan default as long as their NFT collateral has not been seized
    /// @param offer The details of the loan auction offer
    /// @param signature The signature for the offer
    /// @param nftId The id of a specified NFT
    function refinanceByBorrowerSignature(
        Offer calldata offer,
        bytes memory signature,
        uint256 nftId,
        uint32 expectedLastUpdatedTimestamp
    ) external;
}

//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "./ISigLendingEvents.sol";

/// @title NiftyApes interface for the admin role.
interface ISigLendingAdmin {
    /// @notice Updates the associated liquidity contract address
    function updateLendingContractAddress(address newLendingContractAddress) external;

    /// @notice Pauses all interactions with the contract.
    ///         This is intended to be used as an emergency measure to avoid loosing funds.
    function pause() external;

    /// @notice Unpauses all interactions with the contract.
    function unpause() external;
}

//SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

/// @title Events emitted by the signature lending part of the protocol.
interface ISigLendingEvents {
    /// @notice Emitted when the associated liquidity contract address is changed
    /// @param oldLendingContractAddress The old liquidity contract address
    /// @param newLendingContractAddress The new liquidity contract address
    event SigLendingXLendingContractAddressUpdated(
        address oldLendingContractAddress,
        address newLendingContractAddress
    );
}

